import React, {useState} from "react";

const tableStyle = {
    width: "80%",
    margin: "0 auto",
    borderCollapse: "collapse",
}

const columnStyle = {
    width: "20%",
    backgroundColor: "white",
}

const userStyle = {
    backgroundColor: "white",
}
const gap = {
    width: "5%",
}

let newUser1 = "User 1";
let newUser2 = "User 2";
let newUser3 = "User 3";
let newUser4 = "User 4";
let i = 1;
/*
function AddUserSection(props) {
    const [newUser, setNewUser] = useState("");

    const handleUserChange = (e) => {
        setNewUser(e.target.value);
    };

    const handleAddUser = () => {
        if (newUser) {
            props.addUser(newUser);
            setNewUser("");
            if (i === 1) {
                newUser1 = newUser;
            } else if (i === 2) {
                newUser2 = newUser;
            } else if (i === 3) {
                newUser3 = newUser;
            } else {
                newUser4 = newUser;
            }
            TableBody();
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            handleAddUser();
        }
    };

    return (
        <div style={{ display: "flex",
            alignItems: "center",
            justifyContent: "center", }}>
            <label style={{ marginRight: "10px", marginTop: "10px"}}>Add Users:</label>
            <input style={{width: "70%", marginTop: "10px"}}
                type="text"
                value={newUser}
                onChange={handleUserChange}
                onKeyPress={handleKeyPress}
                placeholder="Enter name"
            />
            <button style={{ cursor: "pointer", marginTop: "10px", marginLeft: "20px"}} onClick={handleAddUser}>
                Add
            </button>
        </div>
    );
}
*/

function TableHeader() {
    return (
        <thead>
        <tr>
            <th style={columnStyle}>Name</th>
            <th style={columnStyle}>Quantity</th>
            <th style={columnStyle}>Price</th>
            <th style={gap}></th>
            <th style={userStyle}>Users</th>
        </tr>
        </thead>
    );
}
function TableBody(props) {
    const rows = props.jsonData.map((row, index) => {
            return (
                <tr key={index}>
                    <td style={columnStyle}>{row.description}</td>
                    <td style={columnStyle}>{row.quantity}</td>
                    <td style={columnStyle}>{row.total}</td>
                    <td></td>
                    <td style={userStyle}>
                        <div style={ {display: "inline-table"} }>
                            <button style={{ cursor: "pointer", marginTop: "10px", marginLeft: "10px"}} >{ newUser1 }</button>
                            <button style={{ cursor: "pointer", marginTop: "10px", marginLeft: "10px"}} >{ newUser2 }</button>
                            <button style={{ cursor: "pointer", marginTop: "10px", marginLeft: "10px"}} >{ newUser3 }</button>
                            <button style={{ cursor: "pointer", marginTop: "10px", marginLeft: "10px"}} >{ newUser4 }</button>
                        </div>
                        </td>
                </tr>
            );
        }
    );
    return (
        <tbody>
        {rows}
        </tbody>
    );
}


function Table(props) {

    return (
        <div>
            <table style={tableStyle}>
                <TableHeader />
                <TableBody jsonData={props.jsonData}/>
            </table>
        </div>
    );
}
export default Table;
