import React, {useState} from "react";
import './CSS Files/LoginSignup.css'

const LoginSignup = ({ onLogin }) => {
    const [action, setAction] = useState("Sign Up");
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');

    const handleLoginClick = () => {
        // Validate the username and password here (you may use a backend API)
        // If successful, call the onLogin function to set isLoggedIn to true

        //onLogin();
    };

    const handleSignUpClick = () => {
            const data = {
                username: username,
                password: password,
                email: email,
            };

            // Send a POST request to the /register endpoint
            fetch('http://localhost:8000/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data),
            })
                .then((response) => response.json())
                .then((data) => {
                    console.log(data.message);
                })
                .catch((error) => {
                    console.error(error);
                });
    }

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    }

    return (
        <div className={'cont'}>
            <div className={"header"}>
                <div className={"text"}>{ action }</div>
                <div className={"underline"}></div>
            </div>
            <div className={"inputs"}>
                {action==="Login"?<div></div>:
                    <div className={"input"}>
                        <img src={""} alt={""}/>
                        <input type={"name"} placeholder={"Name"}
                               value={username}
                               onChange={handleUsernameChange}/>
                    </div>}
                <div className={"input"}>
                    <img src={""} alt={""}/>
                    <input type={"email"} placeholder={"Email"}
                        value={email}
                        onChange={handleEmailChange}/>
                </div>
                <div className={"input"}>
                    <img src={""} alt={""}/>
                    <input type={"password"} placeholder={"Password"}
                        value={password}
                        onChange={handlePasswordChange}/>
                </div>
            </div>
            <div className={"submitContainer"}>
                <div className={action==="Login"?"submit grey":"submit"} onClick={()=>{setAction("Sign Up"); handleSignUpClick()}}>Sign Up</div>
                <div className={action==="Sign Up"?"submit grey":"submit"} onClick={()=>{setAction("Login"); handleLoginClick()}}>Login</div>
            </div>
        </div>
    )
}

export default LoginSignup
