import React, {useState, useEffect} from 'react';
import Table from './Table.js'
import Header from './Header.js'
import AddUsers from './AddUsers'
import LoginSignup from "./LoginSignup";
import ImageUpload from './ImageUpload';
import {BrowserRouter as Router, Routes, Route, Navigate} from 'react-router-dom';
import { jwtDecode } from "jwt-decode";

function CreateTable() {
    const [buttonLabels, setButtonLabels] = useState(['User 1', 'User 2', 'User 3']);
    const [jsonData, setJsonData] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [error, setError] = useState(null);


    useEffect(() => {
            fetchData()
                .then((res) =>  res.json())
                .then((json) => setJsonData(json["data"]["line_items"]))
                .catch((error) => {
                    console.error(error);
                    setError(error.message);
                });
            }, []);

    const handleInputChange = (newLabel) => {
        const updatedLabels = [...buttonLabels];

        if (currentIndex < updatedLabels.length) {
            updatedLabels[currentIndex] = newLabel;
        } else {
            updatedLabels.push(newLabel);
        }
        setButtonLabels(updatedLabels);
        setCurrentIndex(currentIndex + 1);
    };

    const Upload = () => {
        return (
            <div>
                <Header />
                <ImageUpload />
            </div>
        );
    };

    const HomePage = () => {
        return (
            <div>
                <Header />
                {error ? (
                    <div style={{ color: 'red', margin: 'auto', maxWidth: '400px', marginTop: '100px' }}>
                        <p>{error}</p>
                    </div>
                ) : (
                    <div>
                        <AddUsers onInputChange={handleInputChange} />
                        <Table jsonData={jsonData} buttonLabels={buttonLabels} />
                    </div>
                )}
            </div>
        );
    };

    const PrivateRoute = ({ element }) => {
        const token = localStorage.getItem('token');

        if (token) {
            const decodedToken = jwtDecode(token);
            if (decodedToken.exp * 1000 > Date.now()) {
                return element;
            }
        }
        return <Navigate to="/" />;
    };

    return (
        <Router>
            <Routes>
                <Route
                    path="/imageUpload"
                    element={<PrivateRoute element={<Upload />} />}
                />
                <Route path="/history" />
                <Route path="/" element={<LoginSignup />} />
                <Route
                    path="/home"
                    element={<PrivateRoute element={<HomePage />} />}
                />
            </Routes>
        </Router>
    );
}

async function fetchData() {
    const token = localStorage.getItem('token');
    const response = await fetch('http://localhost:8000/receipt', {
        method: 'GET',
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });

    if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP error! Status: ${response.status}, ${errorText}`);
    }

    return response.json();
}

export default CreateTable;
